-- 星座の基礎データ
drop table if exists hoshizora_constellation ;
create table hoshizora_constellation (
    horoscope_id                INTEGER             -- 星座ID
  , right_ascension             REAL    not null    -- 赤経
  , declination                 REAL    not null    -- 赤緯
  , constraint PK_hoshizora_constellation primary key ( horoscope_id )
);

delete from hoshizora_constellation ;


